// src/components/Home.js
import React from "react";
import Header from "./Header";
import "./../styles/Home.css";

function Home() {
    const handleLogin = async () => {
        try {
            const response = await fetch("https://08e8-142-58-221-246.ngrok-free.app/auth", {
                method: "GET",
                headers: {
                    "ngrok-skip-browser-warning": "69420", // Required to bypass ngrok's warning page
                },
            });
            if (response.ok) {
                // If successful, redirect to the backend's OAuth flow
                window.location.href = response.url;
            } else {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
        } catch (error) {
            console.error("Error during login:", error);
        }
    };

    return (
        <div className="home-container">
            <Header />
            <div className="home-header">
                <h1>Welcome to Playlist Manager</h1>
                <p>Manage your YouTube playlists with ease.</p>
                <button className="login-button" onClick={handleLogin}>
                    Sign in with Google
                </button>
            </div>
        </div>
    );
}

export default Home;
