import React, { useState, useEffect } from "react";
import Header from "./Header";
import "./../styles/Home.css";

function Home() {
    const [youtubePlaylists, setYouTubePlaylists] = useState([]);
    const [error, setError] = useState(null);

    const fetchYouTubePlaylists = async () => {
        try {
            const response = await fetch("https://3e85-207-102-214-250.ngrok-free.app/playlists/youtube");
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();

            if (data.playlists) {
                setYouTubePlaylists(data.playlists);
            } else {
                setError("No playlists found.");
            }
        } catch (err) {
            console.error("Error fetching YouTube playlists:", err);
            setError(err.message);
        }
    };

    useEffect(() => {
        fetchYouTubePlaylists(); // Fetch YouTube playlists when component mounts
    }, []);

    return (
        <div className="home-container">
            <Header />
            <div className="content">
                <h1>Welcome to Playlist Manager</h1>
                <p>Manage your YouTube and Spotify playlists with ease.</p>

                <div className="playlists-container">
                    <h2>YouTube Playlists</h2>
                    {error ? (
                        <p className="error">{error}</p>
                    ) : youtubePlaylists.length > 0 ? (
                        <ul className="playlist-list">
                            {youtubePlaylists.map((playlist, index) => (
                                <li key={index} className="playlist-item">
                                    {playlist}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>Loading playlists...</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Home;
